import { BuiltInParserName, format } from 'prettier';

/**
 * 格式化代码
 * @param code 代码文本
 * @param parser 代码类型
 */
export const formatCode = (code: string, parser: BuiltInParserName = 'typescript') => {
  return format(code, {
    parser,
    singleQuote: true,
  });
};

export const lowercaseLetters = 'abcdefhijkmnprstwxyz';

/**
 * 获取自动生成头部注释
 * @param fileComment 文件名
 * @param generateName 生成脚本名称
 */
export const getAutoGenerateComment = (fileComment: string, generateName: string) => {
  return `
    /**
     * @file ${fileComment}
     * @author Auto Generated by ${generateName}
     */
  `;
};
